drop database ASSM_JAVA4
create database ASSM_JAVA4
use ASSM_JAVA4

create table movie
(
	Id nvarchar(10),
	Genre nvarchar(50),
	MovieName nvarchar(100),
	MovieYear nvarchar(20),
	Note nvarchar(max),
	Background nvarchar(100),
	Photo nvarchar(100),
	primary key(Id)
)

drop table users
create table users
(
	UserId nvarchar(10),
	UserName nvarchar(50),
	Password nvarchar(100),
	Email nvarchar(100),
	Avatar nvarchar(max),
	Role bit,
	primary key(UserId)
)

Create table Favorite(
	idfv bigint identity(1,1) primary key,
	Id  nvarchar(10),
	UserId nvarchar(10),
	likedate date,
	foreign key (Id) references movie (Id),
	foreign key (UserId) references users (UserId)
);

select * from Favorite
SET IDENTITY_INSERT dbo.favorite On;
